import io from 'socket.io-client';

export const createEmitter = () => {

  const socket = io('http://localhost:4000');

  return [
    store => next => action => {
      socket.emit('action', action);
      return next(action);
    },
    store => {
      socket.on('action', action => {
        store.dispatch(action);
      });
    }]
}
